mapper.update:
update member_tbl set 
	member_status = #{memberStatus} 
where member_no = #{memberNo}

mapper.select:
select product_no as productNo, d_category_no as detailCategoryNo, product_name as productName, product_ea as productEa, product_price as productPrice, product_status as productStatus, product_cost as productCost, gonggu_number as gongguNumber, product_content as productContent, product_discount as productDiscount from product_tbl
where product_status = #{productStatus}
where product_name like '%'||#{productName}||'%'
where product_no = #{productNo}

mapper.update:
update
	delivery_tbl set delivery_status = #{deliveryStatus} 
where delivery_no = #{deliveryNo}

mapper.select:
select ot.order_no as orderNo, ot.member_no as memberNo, ot.product_no as productNo, tb.product_name, tb.product_ea, ot.order_date as orderDate, ot.order_status as orderStatus from order_tbl ot join product_tbl tb on (ot.product_no = tb.product_no)
where product_no = #{productNo}
where member_no == #{memberNo}
where order_no = #{orderNo}

select bt.board_no as boardNo, bt.board_name as boardName, bt.board_writer as boardWriter, bt.board_status as boardStatus, nt.member_no, nt.notify_item, count(distinct nt.board_no) as total from board_tbl bt join notify_tbl nt on (bt.board_no = nt.board_no)
where bt.board_no = #{boardNo}
where bt.board_writer = #{boardWriter}

mapper.update:
update product_tbl set 
	product_Status = #{productStatus} 
where product_no = #{productNo}
