mapper.select:
select member_no as memberNo, member_id as memberId, member_name as memberName, member_email as memberEmail, member_phone as memberPhone, member_addr as memberAddr, member_gender as memberGender, member_bday as memberBday, member_bank as memberBank, member_account as memberAccount, member_status as memberStatus, member_agree as memberAgree, member_date as memberDate from member_tbl
where member_name = #{memberName}
where member_id = #{memberId}
where member_no = #{memberNo}
