mapper.update:
update member_tbl set 
	member_status = #{memberStatus} 
where member_no = #{memberNo}

mapper.select:
select product_no as productNo, d_category_no as detailCategoryNo, product_name as productName, product_ea as productEa, product_price as productPrice, product_status as productStatus, product_cost as productCost, gonggu_number as gongguNumber, product_content as productContent, product_discount as productDiscount from product_tbl
where product_status = #{productStatus}
where product_name like '%'||#{productName}||'%'
where product_no = #{productNo}

mapper.update:
update
	delivery_tbl set delivery_status = #{deliveryStatus} 
where delivery_no = #{deliveryNo}

mapper.select:
select ot.order_no as orderNo, ot.member_no as memberNo, ot.product_no as productNo, tb.product_name, tb.product_ea, to_char(ot.order_date,'yyyy-mm-dd') as orderDate, ot.order_status as orderStatus from order_tbl ot join product_tbl tb on (ot.product_no = tb.product_no)
where product_no = #{productNo}
where member_no == #{memberNo}
where order_no = #{orderNo}

select bt.board_no as boardNo, bt.board_name as boardName, bt.board_writer as boardWriter, bt.board_status as boardStatus, nt.member_no, nt.notify_item, count(distinct nt.board_no) as total from board_tbl bt join notify_tbl nt on (bt.board_no = nt.board_no)
where bt.board_no = #{boardNo}
where bt.board_writer = #{boardWriter}

mapper.update:
update product_tbl set 
	product_Status = #{productStatus} 
where product_no = #{productNo}

mapper.select:
select board_no as boardNo, d_category_no as detailCategoryNo, board_writer as boardWriter, to_char(board_date,'yyyy-mm-dd') as boardDate, board_start as boardStart, board_end as boardEnd, board_status as boardStatus from board_tbl
where board_status = #{boardStatus}
where d_category_no = #{detailCategoryNo }

select pc.category_no as cateogoryNo, sum(od.order_detail_cnt * od.order_detail_price * (pt.product_discount)/100) as totalSales, pt.product_cost as totalCost
from order_detail od join product_tbl pt on (od.product_no = pt.product_no) join detail_category dc on (pt.d_category_no = dc.d_category_no) join product_category pc on (dc.category_no = pc.category_no) join order_tbl ot on (od.order_no = ot.order_no) join pay_tbl pt on (ot.pay_no = pt.pay_no)
where ot.order_status = 1 and pt.pay_status = 1 group by pc.category_no, pt.product_cost;

select sum(od.order_detail_cnt * pdt.product_price * (1-((pdt.product_discount)/100))) as totalSales, sum(pdt.product_cost * od.order_detail_cnt) as totalCost
from order_detail od join product_tbl pdt on (od.product_no = pdt.product_no) join order_tbl ot on (od.order_no = ot.order_no) join pay_tbl pt on (ot.pay_no = pt.pay_no) join member_tbl mt on(ot.member_no = mt.member_no)
where ot.order_status = 1 and pt.pay_status = 1 and to_char(ot.order_date,'yyyy-mm-dd') between #{start} and #{end}

select pc.category_no as categoryNo, sum(od.order_detail_cnt * pdt.product_price * (1-((pdt.product_discount)/100))) as totalSales, sum(pdt.product_cost * od.order_detail_cnt) as totalCost
from order_detail od join product_tbl pdt on (od.product_no = pdt.product_no) join detail_category dc on (pdt.d_category_no = dc.d_category_no) join product_category pc on (dc.category_no = pc.category_no) join order_tbl ot on (od.order_no = ot.order_no) join pay_tbl pt on (ot.pay_no = pt.pay_no)
where ot.order_status = 1 and pt.pay_status = 1 and dc.category_no = ${_parameter} group by pc.category_no
