mapper.update:
update member_tbl set member_phone = #{memberPhone}, member_email = #{memberEmail}, member_zonecode = #{memberZoneCode}, member_addr = #{memberAddr}, member_bank = #{memberBank}, member_account = #{memberAccount}, member_agree = #{memberAgree}
,member_bday = to_date(#{memberBday},'yyyymmdd')
,member_path=#{memberPath}
where member_no=#{memberNo}

update member_tbl
set
	member_pw = #{memberPw}
where 
	member_id = #{memberId}
