mapper.select:
SELECT * FROM (SELECT ROWNUM AS RNUM, P.* FROM (select p.PRODUCT_NO AS productNo, p.D_CATEGORY_NO AS detailCategoryNo, p.PRODUCT_NAME AS productName, p.PRODUCT_EA AS productEa, p.PRODUCT_PRICE AS productPrice, p.PRODUCT_STATUS AS productStatus, p.PRODUCT_COST AS productCost, p.GONGGU_NUMBER AS gongguNumber, p.PRODUCT_CONTENT AS productContent, p.PRODUCT_DISCOUNT AS productDiscount, (SELECT FILEPATH FROM PRODUCT_IMG WHERE P_IMG_NO=(SELECT MIN(P_IMG_NO) FROM PRODUCT_IMG WHERE PRODUCT_NO=p.PRODUCT_NO)) AS thumbnail, pc.category_no as firstCategoryNo from product_tbl p join detail_category dc on p.d_category_no = dc.d_category_no join product_category pc on dc.category_no = pc.category_no where Pc.Category_No = #{firstCategoryNo}
and p.D_CATEGORY_NO = #{detailCategoryNo}
order by 1 DESC) P)
order by productDiscount DESC) P)
order by productPrice DESC) P)
order by productPrice ASC) P)
WHERE RNUM BETWEEN #{start} AND #{end}

select count(*) as totalCount 
from Product_Tbl
left join detail_category using (d_category_no)
where 
category_no = #{firstCategoryNo}

and d_category_no = #{detailCategoryNo}
